import { HttpParams } from '@angular/common/http';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {

  numberOfDays: number = 0;
  destination: string = '';
  vactionPlans: any;
  vacationActivities: any;

  constructor(private router: Router,private http: HttpClient) {}

  vacationDetails()
    {
      let headers = new HttpHeaders({
        'x-rapidapi-host': 'ai-trip-planner.p.rapidapi.com',
        'x-rapidapi-key': 'fb4362bc11mshd9c08352a7e2038p1b7338jsn1434173b1a3d'
      });

      const params = new HttpParams().set
                  ('days', this.numberOfDays).set('destination', this.destination);

      this.http.get('https://ai-trip-planner.p.rapidapi.com/', {
				headers: headers, params: params
			})
			.subscribe((data: any) => {
        this.vactionPlans = data.plan;
				console.log(this.vactionPlans);
			});
    }
}
   <div class="container">
    <nav>
      <img src="../../assets/images/logoorange.png" height="80px" width="80px" class="logo" alt="">
      <br>
      <h4 style="color: orange; font-family: Apple Chancery, cursive;">Vacation Planner</h4>
      <ul>
        <li><a routerLink="/home">Home</a></li>
        <li><a routerLink="/about">About</a></li>
        <li><a routerLink="/contact">Contact Us</a></li>
        <li><a routerLink="/login">Logout</a></li>
      </ul>
    </nav>
    <div class="content">
      <h1 style="font-family: Apple Chancery, cursive; ">Beautiful <br> Places to Explore..</h1>
      <p style="font-family: Apple Chancery, cursive; font-size: 20px;">Close your eyes and imagine being in a place you’ve always wanted to visit. What if you could make that dream a reality? Knowing how to plan and use travel websites can help make a dream trip possible.</p>
      <form>
        <input type="text" placeholder="Country name">
        <input type="text" [(ngModel)]="destination" [ngModelOptions]="{standalone: true}"placeholder="Destination">
        <input type="text" [(ngModel)]="numberOfDays" [ngModelOptions]="{standalone: true}"placeholder="Number of Days">
        <button type="submit" class="btn" (click)="vacationDetails()">Search</button>
        <div class="container">
          <table class="table table-bordered table-striped">
            <thead>
            <tr>
              <th>Day</th>
              <th>Activities</th>
            </tr>
            </thead>
            <tbody>
            <tr *ngFor="let vacationPlan of vactionPlans; let i = index">
              <td><span>{{vacationPlan.day}}</span></td>
              <tr *nfgFor="let activity of vactionPlans">
                <td><span>{{activity.time}}</span></td>
                <td><span>{{activity.description}}</span></td>
            </tr>
            </tbody>
          </table>
        </div>
      </form>
    </div>
   </div>
